    filtrarSolicitudes() {
      if (this.estado == -1) {
        console.log(this.fechaFin, this.fechaInicio);
        this.solicitudesFiltradas = this.solicitudes;
        this.ObtenerDatosPag(1);
      } else {
        this.solicitudesFiltradas = [];

        for (let index = 0; index < this.solicitudes.length; index++) {
          if (this.fechaInicio != "" && this.fechaFin != "") {
            if (this.solicitudes[index].estado === parseInt(this.estado) && this.solicitudes[index].fechaPrestamo >= this.fechaInicio && this.solicitudes[index].fechaPrestamo <= this.fechaFin) {
              this.solicitudesFiltradas.push(this.solicitudes[index]);
              this.ObtenerDatosPag(1);
            }
          } else {
            if (this.solicitudes[index].estado === parseInt(this.estado)) {
              console.log(this.solicitudes[index].fechaPrestamo);
              this.solicitudesFiltradas.push(this.solicitudes[index]);
              this.ObtenerDatosPag(1);
            }
          }
        }

        if (this.solicitudesFiltradas.length === 0) {
          this.paginaPrevia();
        }
      }
    },

    totalPaginas() {
      return Math.ceil(this.solicitudesFiltradas.length / this.elementosPorPagina);
    },

    ObtenerDatosPag(noPag) {
      this.paginaActual = noPag;
      this.datosPaginados = [];
      let ini = noPag * this.elementosPorPagina - this.elementosPorPagina;
      let fin = noPag * this.elementosPorPagina;
      this.datosPaginados = this.solicitudesFiltradas.slice(ini, fin);
    },

    paginaPrevia() {
      if (this.paginaActual > 1) {
        this.paginaActual--;
      }

      this.ObtenerDatosPag(this.paginaActual);
    },

    paginaSiguiente() {
      if (this.paginaActual < this.totalPaginas()) {
        this.paginaActual++;
      }

      this.ObtenerDatosPag(this.paginaActual);
    },

    activo(noPoag) {
