  methods: {
    cambiarRegistrosPorPagina() {
      this.paginaActual = 1;
      this.ObtenerDatosPag(this.paginaActual);
    },

    redireccionaarPdf(pdf) {
      var a = document.createElement("a");
      a.target = "_blank";
      a.href = "http://192.168.0.113:8082/storage/documentos/" + pdf;
      a.click();
