    consultarTipo() {
      return fetch(RUTA_SERVIDOR_LIBROS + "/tipos/").then(respuesta => respuesta.json()).then(datosRespuesta => {
        if (typeof datosRespuesta[0].success === "undefined") {
          datosRespuesta.sort((a, b) => {
            if (a.tipo.toUpperCase() == b.tipo.toUpperCase()) {
              return 0;
            }

            if (a.tipo.toUpperCase() > b.tipo.toUpperCase()) {
              return 1;
            }

            if (a.tipo.toUpperCase() < b.tipo.toUpperCase()) {
              return -1;
            }
          });
          datosRespuesta.forEach(tipo => {
            tipo.stado = "accepted"; // Puedes establecer el valor inicial como falso
          });
          this.tipos = datosRespuesta;
        }
      }).catch(console.log);
    },

    consultarMaterlales() {
      // Llama a consultarTipo() para obtener los tipos antes de consultar los materiales.
      return this.consultarTipo().then(() => {
        return fetch(RUTA_SERVIDOR_LIBROS + "/materialesSubsede/" + this.subsede).then(respuesta => respuesta.json()).then(datosRespuesta => {
          this.Materiales = [];

          if (typeof datosRespuesta[0].success === "undefined") {
            datosRespuesta.sort((a, b) => {
              if (a.titulo.toUpperCase() == b.titulo.toUpperCase()) {
                return 0;
              }

              if (a.titulo.toUpperCase() > b.titulo.toUpperCase()) {
                return 1;
              }

              if (a.titulo.toUpperCase() < b.titulo.toUpperCase()) {
                return -1;
              }
            });
            this.Materiales = datosRespuesta;
            this.Materiales.forEach(material => {
              const tipoAsociado = this.tipos.find(tipo => tipo.id == material.idTipomaterial);

              if (tipoAsociado) {
                material.tipo = tipoAsociado.tipo;
              }
            });
            console.log(this.Materiales);

            for (let index = 0; index < this.Materiales.length; index++) {
              for (let index2 = 0; index2 < this.autor.length; index2++) {
                if (this.autor[index2].id == this.Materiales[index].idAutor) {
                  var nombreAutor = this.autor[index2].nombre;
                  this.Materiales[index].nombre = nombreAutor;
                }
              }
            }

            this.filtrarMateriales();
            this.filtrarArchivos();
          }
        }).catch(console.log);
      });
    },

    filtrarArchivos() {
      this.dataToExport = [];

      for (let index = 0; index < this.Materiales.length; index++) {
        if (this.Materiales[index].idCarrera === this.materia && this.Materiales[index].activo === 1 && this.Materiales[index].documento != null && this.radioPdf == 1) {
          this.dataToExport = this.dataToExport.concat(this.Materiales[index]);
        } else {
          if (this.Materiales[index].idCarrera === this.materia && this.Materiales[index].activo === 1 && this.Materiales[index].documento == null && this.radioPdf == 0) {
            this.dataToExport = this.dataToExport.concat(this.Materiales[index]);
          }
        }
      }
    },

    filtrarMateriales() {
      this.totalMateriales = [];

      for (let index = 0; index < this.Materiales.length; index++) {
        if (this.Materiales[index].idCarrera === this.materia && this.Materiales[index].activo === 1 && (this.Materiales[index].titulo.toUpperCase().includes(this.nombreMaterial.toUpperCase()) || this.Materiales[index].nombre.toUpperCase().includes(this.nombreMaterial.toUpperCase())) && this.Materiales[index].documento != null && this.radioPdf == 1) {
          if (this.tipos.some(tipo => tipo.stado == "accepted" && tipo.id == this.Materiales[index].idTipomaterial)) {
            this.totalMateriales = this.totalMateriales.concat(this.Materiales[index]);
            this.ObtenerDatosPag(1);
          }
        } else {
          if (this.Materiales[index].idCarrera === this.materia && this.Materiales[index].activo === 1 && (this.Materiales[index].titulo.toUpperCase().includes(this.nombreMaterial.toUpperCase()) || this.Materiales[index].nombre.toUpperCase().includes(this.nombreMaterial.toUpperCase())) && this.Materiales[index].documento == null && this.radioPdf == 0) {
            if (this.tipos.some(tipo => tipo.stado == "accepted" && tipo.id == this.Materiales[index].idTipomaterial)) {
              this.totalMateriales = this.totalMateriales.concat(this.Materiales[index]);
              this.ObtenerDatosPag(1);
            }
          }
        }
      }

      if (this.totalMateriales.length === 0) {
        this.paginaPrevia(); //this.paginaActual=0;
      }
    },

    consultarAutor() {
      fetch(RUTA_SERVIDOR_LIBROS + "/autor/").then(respuesta => respuesta.json()).then(datosRespuesta => {
        this.autor = datosRespuesta;
        this.consultarMaterlales();
      }).catch(console.log);
    },

    consultarEditorial() {
      fetch(RUTA_SERVIDOR_LIBROS + "/editoriales/").then(respuesta => respuesta.json()).then(datosRespuesta => {
        if (typeof datosRespuesta[0].success === "undefined") {
          this.editoriales = datosRespuesta;
        }
      }).catch(console.log);
    },

    Enviar(id) {
      this.$emit("recibiendo", id);
      listar.editar();
    },

    confirmarEliminarSolicitud(material) {
      if (window.confirm("쮼st치s seguro de que deseas eliminar este material?")) {
        this.eliminar(material);
      }
    },

    eliminar(material) {
      axios.put(RUTA_SERVIDOR_LIBROS + "/materiales/" + material.id, {
        titulo: material.titulo,
        copias: material.copias,
        copiasDisponibles: material.copiasDisponibles,
        idTipomaterial: material.idTipomaterial,
        edicion: material.edicion,
        codigo: material.codigo,
        idAutor: parseInt(material.idAutor),
        idEmpleado: material.idEmpleado,
        idEditorial: parseInt(material.idEditorial),
        idCarrera: parseInt(material.idCarrera),
        urlPortada: material.urlPortada,
        observacion: material.observacion,
        documento: material.documento,
        activo: 0
      }).then(function (response) {
        location.reload();
      }).catch(function (error) {
        console.log(error); // console.log(this.material);
      });
    },

    obtenerMaterial(id, nombre, autor, editorial, edicion, urlPortada, tipo) {
      this.prestamoMaterial = {
        id: id,
        nombreMaterial: nombre,
        autor: autor,
        editorial: editorial,
        edicion: edicion,
        urlPortada: urlPortada,
        tipo: tipo
      };
    },

    consultarCarreras() {
      fetch(RUTA_SERVIDOR_LIBROS + "/carreras").then(respuesta => respuesta.json()).then(datosRespuesta => {
        this.carreras = [];

        if (typeof datosRespuesta[0].success === "undefined") {
          this.carreras = datosRespuesta;
        }
      }).catch(console.log);
    },

    consultarCantidad() {
      fetch(RUTA_SERVIDOR_LIBROS + "/cantidadMateriales/" + this.subsede).then(respuesta => respuesta.json()).then(datosRespuesta => {
        if (typeof datosRespuesta[0].success === "undefined") {
          for (let index = 0; index < datosRespuesta[1].length; index++) {
            this.chartDataL.labels.push(datosRespuesta[1][index][0] + " : " + datosRespuesta[1][index][1]);
            this.chartDataL.datasets[0].data.push(datosRespuesta[1][index][1]);
            this.totalesL += datosRespuesta[1][index][1];
            this.totalesD += datosRespuesta[2][index][1];
            this.dataBar.labels.push(datosRespuesta[1][index][0]);
            this.dataBar.datasets[0].data.push(datosRespuesta[1][index][1]);
            this.dataBar.datasets[1].data.push(datosRespuesta[2][index][1]);
          } // this.data.labels.push(datosRespuesta[1][index][0]+ ' : '+datosRespuesta[1][index][1] )


          for (let index = 0; index < datosRespuesta[0].length; index++) {
            this.chartDataP.labels.push(datosRespuesta[0][index][0] + " : " + datosRespuesta[0][index][1]);
            this.chartDataP.datasets[0].data.push(datosRespuesta[0][index][1]);
          }
        }

        this.dataBar.datasets[0].label.push("Materiales Totales : " + this.totalesL);
        this.dataBar.datasets[1].label.push("Materiales Disponibles : " + this.totalesD);
      }).catch(console.log);
    },

    totalPaginas() {
      if (this.elementosPorPagina === -1) {
        return 1; // Mostrar todo en una sola p치gina
      }

      return Math.ceil(this.totalMateriales.length / this.elementosPorPagina);
    },

    ObtenerDatosPag(noPag) {
      this.paginaActual = noPag;
      this.datosPaginados = [];
      let ini = noPag * this.elementosPorPagina - this.elementosPorPagina;
      let fin = noPag * this.elementosPorPagina;
      this.datosPaginados = this.totalMateriales.slice(ini, fin);
    },

    paginaPrevia() {
      if (this.paginaActual > 1) {
        this.paginaActual--;
      }

      this.ObtenerDatosPag(this.paginaActual);
    },

    paginaSiguiente() {
      if (this.paginaActual < this.totalPaginas()) {
        this.paginaActual++;
      }

      this.ObtenerDatosPag(this.paginaActual);
    },

    activo(noPoag) {
      return noPoag === this.paginaActual ? "active" : "";
    },

    precionadoBoton(id) {
      console.log(id);
    },

    cambiaC(carrera) {
      this.materia = carrera;
      this.filtrarMateriales();
      this.filtrarArchivos();
    },

    exportExcel() {
      const tabla = document.createElement("table");
      const thead = tabla.createTHead();
      const tbody = tabla.createTBody(); // Agregar encabezados de columna a la tabla

      const encabezados = ["Id", "Tipo", "Titulo", "Autor", "Editorial", "Edicion", "Codigo", "Copias", "Disponibles"];
      const encabezadosRow = thead.insertRow();
      encabezados.forEach(encabezado => {
        const th = document.createElement("th");
        th.innerText = encabezado;
        encabezadosRow.appendChild(th);
      }); // Agregar filas de datos a la tabla

      this.datosPaginados.forEach(material => {
        const fila = tbody.insertRow();
        fila.insertCell().innerText = material.id;
        fila.insertCell().innerText = material.tipo;
        fila.insertCell().innerText = material.titulo;
        fila.insertCell().innerText = material.nombre;
        fila.insertCell().innerText = this.editoriales[[material.idEditorial] - 1].nombreEditorial, fila.insertCell().innerText = material.edicion;
        fila.insertCell().innerText = material.codigo;
        fila.insertCell().innerText = material.copias;
        fila.insertCell().innerText = material.copiasDisponibles;
      }); // Crear un archivo Blob para la tabla HTML

      const blob = new Blob([tabla.outerHTML], {
        type: "application/vnd.ms-excel"
      });
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = "Materiales.xls";
      link.click();
    },

    exportPdf() {
      var doc = new jsPDF(); //titulo

      doc.setFontSize(16);
      var title = "Materiales";
      var titleWidth = doc.getStringUnitWidth(title) * doc.internal.getFontSize() / doc.internal.scaleFactor;
      var pageWidth = doc.internal.pageSize.getWidth();
      var x = (pageWidth - titleWidth) / 2;
      doc.text(title, x, 10); // Obtener la tabla visible

      var table = document.querySelector(".tableMb"); // Obtener la tabla visible

      var table = document.querySelector(".tableMb"); // Configurar los estilos de la tabla

      var styles = {
        overflow: "linebreak",
        fontSize: 8,
        // Tama침o de fuente reducido
        lineWidth: 0.1,
        lineColor: [0, 0, 0],
        // Color de las l칤neas
        font: "helvetica",
        fontStyle: "normal",
        halign: "center",
        // Alineaci칩n horizontal
        valign: "middle",
        // Alineaci칩n vertical
        cellPadding: 2,
        tableWidth: "auto",
        tableBorder: 1,
        tableLineColor: [0, 0, 0],
        // Color del borde de la tabla
        tableFillColor: [255, 255, 255],
        // Color de fondo de la tabla
        textColor: 0,
        cellWidth: "auto" // Ajustar el ancho de la celda autom치ticamente

      }; // Ocultar la 칰ltima columna (Acci칩n) antes de agregar la tabla al documento PDF

      var actionColumn = table.querySelector("th:last-child, td:last-child");

      if (actionColumn) {
        actionColumn.style.display = "none";
      } // Agregar la tabla al documento PDF


      doc.autoTable({
        html: table,
        startY: 20,
        styles
      }); // Mostrar la 칰ltima columna nuevamente

      if (actionColumn) {
        actionColumn.style.display = "table-cell";
      } // Guardar el PDF


      doc.save("Materiales.pdf");
    },

    closeModal() {
      this.$refs.modalCenter.hide();
